panel.onshow:
If(!cc_serverlist_updating,Split(Set('cc_serverlist_updating',true),RefreshServerList()));
If(WidgetExists('ccpushbutton_ccwidget_community_panel'),Split(HideWidget('ccpushbutton_ccwidget_community_button'),ShowWidget('ccpushbutton_ccwidget_community_button_down')));
If(cc_community_X-20+258 lt 0,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
If(cc_community_Y-20+440 lt 0,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
If(cc_community_X+20 gt vid_width,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
If(cc_community_Y+20 gt vid_height-90,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
If(!cc_community_X,Set('cc_community_X',vid_width-258+1));
If(!cc_community_Y,Set('cc_community_Y',vid_height / 2 - 300+1));
SetX(cc_community_X-1); SetY(cc_community_Y-1);

panel.onhide:
If(WidgetExists('ccpushbutton_ccwidget_community_panel'),Split(HideWidget('ccpushbutton_ccwidget_community_button_down'),ShowWidget('ccpushbutton_ccwidget_community_button')));
    If(cc_community_X-20+258 lt 0,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
    If(cc_community_Y-20+440 lt 0,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
    If(cc_community_X+20 gt vid_width,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
    If(cc_community_Y+20 gt vid_height-90,Split(Set('cc_community_X',0),Set('cc_community_Y',0)));
    If(!cc_community_X,Set('cc_community_X',vid_width-258+1));
    If(!cc_community_Y,Set('cc_community_Y',vid_height / 2 - 300+1));
    SetX(cc_community_X-1); SetY(cc_community_Y-1);

listbox.onrightclick:
Set('ui_lastSelectedUser', Substring(this,1,-1));
          if(Substring(this,0,1) == '0', Set('ui_tooltipTarget', ''));
          if(Substring(this,0,1) == '1', Set('ui_tooltipTarget', 'friendlist_rtclick_online'));
          if(Substring(this,0,1) == '2', Set('ui_tooltipTarget', ''));
          if(Substring(this,0,1) == '3', Set('ui_tooltipTarget', 'friendlist_rtclick_game'));
          if(Substring(this,0,1) == '4', Set('ui_tooltipTarget', ''));
          if(Substring(this,0,1) == '5', Set('ui_tooltipTarget', 'friendlist_rtclick_offline'));
          if(StringLength(ui_tooltipTarget) gt 0, Call(ui_tooltipTarget, 'SetVisible(true);'));
          if(StringLength(ui_tooltipTarget) gt 0, Call(ui_tooltipTarget, 'SetX(\'' # param0 # '\');'));
          if(StringLength(ui_tooltipTarget) gt 0, Call(ui_tooltipTarget, 'SetY(\'' # param1 # '\');'));
          if(StringLength(ui_tooltipTarget) gt 0, Call(ui_tooltipTarget # '_choices', 'ListBoxCmd(\'SelectNone\');'));
          If(StringLength(ui_tooltipTarget),PlaySound('/shared/sounds/ui/button_rightclick.mp3'));
          If(StringLength(ui_tooltipTarget),SetFocusName(ui_tooltipTarget),SetFocus(1));

Set('ui_lastSelectedUser', Substring(this,2,-1));
						if(Substring(this,0,2) == '00', Set('ui_tooltipTarget', ''));
