instance.onclick:
If(cc_{parentid}_Height_Temp == 0,Set('cc_{parentid}_Height_Temp',cc_{parentid}_Height));
If(cc_{parentid}_Width_Temp == 0,Set('cc_{parentid}_Width_Temp',cc_{parentid}_Width));
SetSave('cc_{parentid}_RememberPosition',cc_{parentid}_RememberPosition_Temp);
If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_X'));
If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_Y'));
Call('cceditbox_{parentid}_resizeheight_textbox','If(cc_{parentid}_RememberPosition,SetSave(\'cc_{parentid}_Height\',cc_{parentid}_Height_Temp),Set(\'cc_{parentid}_Height\',cc_{parentid}_Height_Temp)); EraseInputLine();');
Call('cceditbox_{parentid}_resizewidth_textbox','If(cc_{parentid}_RememberPosition,SetSave(\'cc_{parentid}_Width\',cc_{parentid}_Width_Temp),Set(\'cc_{parentid}_Width\',cc_{parentid}_Width_Temp)); EraseInputLine();');
Call('ccwidget_{parentid}','SetHeight(cc_{parentid}_Height+{parentheaderheight=0}+(If({parentfooter=1},{parentfooterheight=0},0))); SetWidth(cc_{parentid}_Width);');
Call('ccwidget_{parentid}','SetX(cc_{parentid}_X-1); SetY(cc_{parentid}_Y-1);');
HideWidget('ccwidget_{parentid}_resize');
SetFocusName('ccwidget_{parentid}');

instance.onenter:
Set('cc_{parentid}_Height_Temp',this); If(cc_{parentid}_Height_Temp == 0,
Set('cc_{parentid}_Height_Temp',cc_{parentid}_Height));
If(cc_{parentid}_Width_Temp == 0, Set('cc_{parentid}_Width_Temp',cc_{parentid}_Width));
SetSave('cc_{parentid}_RememberPosition',cc_{parentid}_RememberPosition_Temp); If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_X'));
If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_Y'));
If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_Height',cc_{parentid}_Height_Temp),
Set('cc_{parentid}_Height',cc_{parentid}_Height_Temp)); EraseInputLine(); Call('cceditbox_{parentid}_resizewidth_textbox','
If(cc_{parentid}_RememberPosition,SetSave(\'cc_{parentid}_Width\',cc_{parentid}_Width_Temp), Set(\'cc_{parentid}_Width\',cc_{parentid}_Width_Temp)); EraseInputLine();'); Call('ccwidget_{parentid}','SetHeight(cc_{parentid}_Height+{parentheaderheight=0}+(If({parentfooter=1},{parentfooterheight=0},0))); SetWidth(cc_{parentid}_Width);'); Call('ccwidget_{parentid}','SetX(cc_{parentid}_X-1); SetY(cc_{parentid}_Y-1);'); HideWidget('ccwidget_{parentid}_resize');

t('cc_{parentid}_Width_Temp',this);
If(cc_{parentid}_Height_Temp == 0,Set('cc_{parentid}_Height_Temp',cc_{parentid}_Height));
If(cc_{parentid}_Width_Temp == 0,Set('cc_{parentid}_Width_Temp',cc_{parentid}_Width)); If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_X'));
If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_Y')); Call('cceditbox_{parentid}_resizeheight_textbox',' If(cc_{parentid}_RememberPosition,SetSave(\'cc_{parentid}_Height\',cc_{parentid}_Height_Temp),
Set(\'cc_{parentid}_Height\',cc_{parentid}_Height_Temp)); EraseInputLine();'); If(cc_{parentid}_RememberPosition,SetSave('cc_{parentid}_Width',cc_{parentid}_Width_Temp),Set('cc_{parentid}_Width',cc_{parentid}_Width_Temp)); EraseInputLine(); Call('ccwidget_{parentid}','SetHeight(cc_{parentid}_Height+{parentheaderheight=0}+(If({parentfooter=1},{parentfooterheight=0},0))); SetWidth(cc_{parentid}_Width);'); Call('ccwidget_{parentid}','SetX(cc_{parentid}_X-1); SetY(cc_{parentid}_Y-1);'); HideWidget('ccwidget_{parentid}_resize');

panel.onshow:
If(cc_{id}{nick}_X-20+cc_{id}{nick}_Height lt 0,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_Y-20+cc_{id}{nick}_Width lt 0,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_X+20 gt vid_width,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_Y+20 gt vid_height-90,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_RememberPosition,
If(cc_{id}{nick}_Maximised,Split(SetX(0), SetY(0), SetWidth(-2), SetHeight(-1), HideWidget('ccwidget_{id}{nick}_maximise'),
ShowWidget('ccwidget_{id}{nick}_restore'),
ShowWidget('ccwidget_{id}{nick}_blocker'),
If(!{canresize=1},HideWidget('ccwidget_{id}{nick}_restore'))),Split(SetX(cc_{id}{nick}_X-1),
SetY(cc_{id}{nick}_Y-1),
SetHeight(cc_{id}{nick}_Height+{headerheight=22}+(If({footer=1},{footerheight=28},0))),
SetWidth(cc_{id}{nick}_Width))));

panel.onhide:
If(WidgetExists('ccpushbutton_ccwidget_{id}{nick}_panel'),Split(HideWidget('ccpushbutton_ccwidget_{id}{nick}_button_down'),ShowWidget('ccpushbutton_ccwidget_{id}{nick}_button')));
If(cc_{id}{nick}_X-20+cc_{id}{nick}_Height lt 0,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_Y-20+cc_{id}{nick}_Width lt 0,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_X+20 gt vid_width,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));
If(cc_{id}{nick}_Y+20 gt vid_height-90,Split(Set('cc_{id}{nick}_X',{x=36}+1),Set('cc_{id}{nick}_Y',{y=90}+1))));

panel.onwake:
If(_cc_alpha lt 0,Split(
							Set('_cc_alpha',_cc_alpha - 4), SetColor(0,0,0,(119 / 256)-(-1*(_cc_alpha / 256))), If (_cc_alpha gt -119,SleepWidget(1))
						),Split(
							Set('_cc_alpha',_cc_alpha + 4), SetColor(0,0,0,_cc_alpha / 256), If (_cc_alpha lt 119,SleepWidget(1))
						));
