table.onclick:
_valid_server = (dataid != -1);
If(_valid_server, Set(_valid_server, dataid != -2)));
If(_valid_server, Set(_valid_server, (dataid != 0)));
If(_valid_server, Set(_browser_last_selected_server, dataid));
If(_valid_server, Call('server_list_selected', 'SetVisible(true); SetY(\'' # GetCellPositionY(col, row) # '\');'));
If(dataid == -2 and _last_sorted == col, Set(_reverse_sort, 1));
If(dataid == -2 and _last_sorted != col, Set(_reverse_sort, 0));
If(_reverse_sort and dataid == -2 and col == 1, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 5, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 6, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 7, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 8, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col != 1 and col != 5 and col != 6 and col != 7 and col != 8, SortByColReverse(col));
If(_reverse_sort and dataid == -2, Set(_last_sorted, -1));
If(!_reverse_sort and dataid == -2 and col == 1, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 5, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 6, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 7, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 8, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col != 1 and col != 5 and col != 6 and col != 7 and col != 8, SortByCol(col));
If(!_reverse_sort and dataid == -2, Set(_last_sorted, col));

table.ondoubleclick:
_valid_server = (dataid != -1);
If(_valid_server, Set(_valid_server, dataid != -2)));
If(_valid_server, Set(_valid_server, (dataid != 0)));
If(_valid_server, Set(_browser_last_selected_server, dataid));
If(_valid_server, Call('server_list_selected', 'SetVisible(true); SetY(\'' # GetCellPositionY(col, row) # '\');'));
If(_valid_server, Disconnect());
If(_valid_server and !IsServerNumPassworded(dataid), ConnectToServerNum(dataid));
If(_valid_server and IsServerNumPassworded(dataid), ShowWidget('connect_password'));
If(dataid == -2 and _last_sorted == col, Set(_reverse_sort, 1));
If(dataid == -2 and _last_sorted != col, Set(_reverse_sort, 0));
If(_reverse_sort and dataid == -2 and col == 1, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 5, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 6, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 7, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col == 8, SortByColNumReverse(col));
If(_reverse_sort and dataid == -2 and col != 1 and col != 5 and col != 6 and col != 7 and col != 8, SortByColReverse(col));
If(_reverse_sort and dataid == -2, Set(_last_sorted, -1));
If(!_reverse_sort and dataid == -2 and col == 1, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 5, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 6, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 7, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col == 8, SortByColNum(col));
If(!_reverse_sort and dataid == -2 and col != 1 and col != 5 and col != 6 and col != 7 and col != 8, SortByCol(col));
If(!_reverse_sort and dataid == -2, Set(_last_sorted, col));

listbox.onshow:
ListBoxCmd('Clear');
ForEachFile('!', '/maps', '*.s2z', true, 'ListBoxCmd(\'Add \' # Substring(file_filepath, 6, StringLength(file_filepath) - 10));');
ForEachFile('!', '@/maps', '*.s2z', true, 'ListBoxCmd(\'Add \' # Substring(file_filepath, 7, StringLength(file_filepath) - 11));');
ListBoxCmd('Sort');
ListBoxCmd('Select 0');
Set(_game_mapname, this);

ListBoxCmd('Clear');
ForEachFile('!', '/maps', '*.s2z', true, 'ListBoxCmd(\'Add \' # Substring(file_filepath, 6, StringLength(file_filepath) - 10));');
ForEachFile('!', '@/maps', '*.s2z', true, 'ListBoxCmd(\'Add \' # Substring(file_filepath, 7, StringLength(file_filepath) - 11));');
ListBoxCmd('Sort');
ListBoxCmd('Select 0');
Set(_game_mapname, this);

ListBoxCmd('Clear');
ForEachFile('!', '/maps', '*.s2z', true, 'ListBoxCmd(\'Add \' # Substring(file_filepath, 6, StringLength(file_filepath) - 10));');
ListBoxCmd('Sort');
ListBoxCmd('Select 0');
Set(_game_mapname, this);

button.onclick:
CreateString('svr_connectPass', _game_password);
							CreateString('cl_password', _game_password);
							if(StringLength(_game_mapname) > 0, Split(StartLocalGame(_game_mapname), HideWidget('create_game_panel'), HideWidget('local_browser_panel')));
